import React, { Component, useEffect, useState } from 'react';
import socket from "../socket";
import User from "./User";
import MessagePanel from "./MessagePanel";
import "./Chat.scoped.css";

const Chat = () => {
    const [selectedUser, setSelectedUser] = useState(null);
    const [users, setUsers] = useState([]);


    socket.on("connect", () => {
        console.log("clientconnect");
        users.forEach((user) => {
            if (user.self) {
                user.connected = true;
            }
        });
    });

    socket.on("disconnect", () => {
        users.forEach((user) => {
            if (user.self) {
                user.connected = false;
            }
        });
    });

    socket.on("users", (userss) => {
        userss.forEach((user) => {
            console.log("user : ", user);
            user.self = user.userID === socket.id; 
            initReactiveProperties(user);
        });
        // put the current user first, and sort by username
        var userstmp = []
        userstmp = userss.sort((a, b) => {
            if (a.self) return -1;
            if (b.self) return 1;
            if (a.username < b.username) return -1;
            return a.username > b.username ? 1 : 0;
        });
        setUsers(userstmp);
        console.log("userstmp : ", userstmp);

    });

    socket.on("user connected", (user) => {
        initReactiveProperties(user);
        setUsers(users.concat(user));
        console.log("user connected : ", users);
        //users.push(user);
    });

    socket.on("user disconnected", (id) => {
        for (let i = 0; i < users.length; i++) {
            const user = users[i];
            if (user.userID === id) {
                user.connected = false;
                setUsers(users);
                break;
            }
        }
    });

    useEffect(()=> {
        socket.on("private message", ({ content, from }) => {
            for (let i = 0; i < users.length; i++) {
                const user = users[i];
                if (user.userID === from) {
                    console.log("content", content);        
                    user.messages.push({
                        content,
                        fromSelf: false,
                    });
                    if (user !== selectedUser) {
                        user.hasNewMessages = true;
                    }
                    //setUsers(users);
                    break;
                }
            }

        });
        return () => {
            console.log("UNMOUNT")
            socket.off("private message");
        }
    });



    // useEffect(() => {
    //     return () => {
    //         console.log("UNMOUNT")
    //         socket.off("connect");
    //         socket.off("disconnect");
    //         socket.off("users");
    //         socket.off("user connected");
    //         socket.off("user disconnected");
    //         socket.off("private message");
    //     }
    // }, []);



    const initReactiveProperties = (user) => {
        user.connected = true;
        user.messages = [];
        user.hasNewMessages = false;
    };

    const getSelectUser = (selectuser) => {
        setSelectedUser(selectuser);
        selectuser.hasNewMessages = false;
        console.log("selectuser : ", selectuser);
    }

    const onMessage = (content) => {
        //console.log("content : ", content);
        if(selectedUser){
            //console.log("selectedUser : ", selectedUser);
            socket.emit("private message", {
                content,
                to: selectedUser.userID,
            });
            selectedUser.messages.push({
                content,
                fromSelf: true,
            });
            setSelectedUser(selectedUser);
        }
    }
    // const onSelectUser = (user) => {
    //     setSelectedUser(user);
    //     user.hasNewMessages = false;
    // }

    return(
        <div>
            <div className="left-panel">
                {
                    users.map(user => (
                        <User user={user} selected={selectedUser === user} getSelectUser={getSelectUser}/>
                    ))
                }
            </div>
            <div className="right-panel">
                {
                    selectedUser?
                    <MessagePanel 
                        user = {selectedUser}
                        onMessage = {onMessage}
                        className="right-panel"
                    /> : null
                }

            </div>
        </div>
    );
}

export default Chat;
