import React, { Component, useState } from 'react';
import "./SelectUsername.css";

export default function SelectUsername() {
    const [username, setUsername] = useState('');
    //const [isValid, setIsValid] = useState(false);
    const onSubmit = (event) => {
        event.preventDefault();
        // setUsername(event.target.value);
        console.log(username);
    }
    const isValid = (username) => {
        console.log(username.length);
        return username.length > 2;
    }

    return (
        <div className="select-username">
            <form onSubmit={onSubmit}>
                <input placeholder="Your username..." onChange={e => setUsername(e.target.value)}/>
                <button disabled={!isValid(username)}>Send</button>
            </form>
        </div>
    );
}

