import React, { Component, useState } from 'react';
import StatusIcon from "./StatusIcon";
import "./User.css";

const User = ({ user, selected, getSelectUser }) => {

    const onClick = () => {
        console.log("click!!");
        getSelectUser(user);
    }

    return(
        <div className={`${selected ? 'selected' : ''} user`} onClick={onClick}> 
            <div className="description">
                <div className="name">
                    { user.username } { user.self ? " (yourself)" : "" }
                </div>
                <div className="status">
                    <StatusIcon connected={user.connected}/> { user.connected ? "online" : "offline" }
                </div>
                {user.hasNewMessages ? <div className="new-messages">!</div> : <div></div>}
            </div>

        </div>
    );
}
export default User;
