import React, { Component, useState } from 'react';
import Player from './Player';
import paper from './paper.png';
import rock from './rock.png';
import scissors from './scissors.png';
import '../App.css';
import "./Game.scoped.css";

function Game(props) {

    function click() {
        console.log("clicked!!");
    }
    return (
        <div>
            <div className="left-panel">
                <h2 className="roomname">{props.value} Room</h2>
                <Player/>
            </div>

            <div className="right-panel">

                <div>
	                <div>

                        <button id="rock" className="btn" value="rock"><img className="bn" src={rock} onClick={click}/></button>
                        <button id="paper" className="btn" value="paper"><img className="bn" src={paper} onClick={click}/></button>
                        <button id="scissors" className="btn" value="scissors"><img className="bn" src={scissors} onClick={click}/></button>
	                </div>
                </div>

            </div>
        </div>

    )
}

export default Game;
