import React, { Component, useState } from 'react';
import Player from './Player';
import gameicon from './game.png';
import '../App.css';
import "./Game.scoped.css";

function Game(props) {
    return (
        <div>
            <div className="left-panel">
                <h2 className="roomname">{props.value} Room</h2>
                <Player/>
            </div>

            <div className="right-panel">

                <div class="container">
	                <div class="score-container">
                        <span class="user">YOU</span>
                        <span id="score"></span>
                        <span class="com">COM</span>
	                </div>
	                <div class="buttons">
                        <button id="paper" class="bn" onclick="game(this.value)" value="paper"><img src={gameicon}/></button>
                        <button id="rock" class="bn" onclick="game(this.value)" value="rock"><i class="far fa-hand-rock"></i></button>
                        <button id="scissors" class="bn" onclick="game(this.value)" value="scissors"><i class="far fa-hand-scissors"></i></button>
	                </div>
                </div>

            </div>
        </div>

    )
}

export default Game;
