import React, { Component, useState } from 'react';
import Player from './Player';
import gameicon from './game.png';
import '../App.css';
import "./Game.scoped.css";

function Game(props) {

    function click() {
        console.log("clicked!!");
    }
    return (
        <div>
            <div className="left-panel">
                <h2 className="roomname">{props.value} Room</h2>
                <Player/>
            </div>

            <div className="right-panel">

                <div class="container">
	                <div class="score-container">
                        <span class="user">YOU</span>
                        <span id="score"></span>
                        <span class="com">COM</span>
	                </div>
	                <div>
                        <button id="paper" className="btn" value="paper"><img className="bn" src={gameicon} onClick={click}/></button>
                        <button id="rock" className="btn" value="rock"><img className="bn" src={gameicon} onClick={click}/></button>
                        <button id="scissors" className="btn" value="scissors"><img className="bn" src={gameicon} onClick={click}/></button>
	                </div>
                </div>

            </div>
        </div>

    )
}

export default Game;
